const TarantoolConnection = require('./index');

const connection = new TarantoolConnection({host: 'localhost', port: 33013, username: 'test', password: 'test'});
(async () => {
  await connection.insert('tags', ['tag_1', 1])
  await connection.insert('tags', ['tag_2', 50])
  connection.sql('select * from "tags"')
    .then((res) => {
      console.log('Successful get tags', res);
    })
    .catch((error) => {
      console.log(error);
    });
})()


